INSERT INTO `book` VALUES ('21', '三国之兵临天下', '三国之兵临天下', '12496', '1', '1131', '3696794', 'http://api.luffybook.com/uploads/20170523/bf8eee1d470774d6f9f16c01911b0d59.jpg', '3347863', '7', '3', '{\"3\":\"奇幻\"}', '[\"2\"]', '一个穿越千年的灵魂，一名卑微的少年，一辆神秘的牛车，命运交织，演绎出一段金戈铁马的传奇。波澜壮阔的三国新争霸史由此拉开了序幕。', '{\"gather_id\":\"5\",\"list_url\":\"http:\\/\\/www.23us.com\\/html\\/60\\/60882\\/\"}', '2017-05-18 16:59:11', '2017-05-27 11:28:04');
